sys_widgets.script:
function sections() {
    return {
        'LogIn': { 'uiPageName' : 'Test_UI_Page'}

    };
}

function render() {
	try{

    var scope = gs.getCurrentScopeName();


    scope = (scope == "rhino.global" ? "" : scope + "_");
    var uiPageName = renderer.getPreference('uiPageName');
// 		return " "+uiPageName;

    return renderer.getRenderedPage(scope+uiPageName);

	}
	catch(ex){
// 		gs.addErrorMessage(ex);
		return " "+ex;
	}
}

function getEditLink() {
	try{

    var scope = gs.getCurrentScopeName();


    scope = (scope == "rhino.global" ? "" : scope + "_");
    var uiPageName = renderer.getPreference('uiPageName');
    return 'sys_ui_page.do?sysparm_query=' + encodeURIComponent('name=' + uiPageName);
		}
	catch(ex){
// 		gs.addErrorMessage(ex);
		return "From HERE : "+ex;
	}
}
